
public class Numeros{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     System.out.println("======Numeros=====");
	
     for (int  num= 1; num <= 3; num++) {
         for (int row = 1; row <= 3; row++) {
             System.out.println(num + " " + row);
         }
     }
 }
}
    	        
    	        
    	       

	


