
public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("======Do While=====");
		int cont=0;
		do {
            System.out.println(cont); // Imprime el valor de cont
            cont++; // Aumenta cont en 1
        } while (cont < 100); // La condiciÃ³n es que cont sea menor que 100 do while
	}//main
	
	}//do while


